import React, { useState } from "react";
import axios from "axios";
import Board from "./components/Board";

const App = () => {
  const [gameId, setGameId] = useState("");
  const [player, setPlayer] = useState("");
  const [gameState, setGameState] = useState(null);
  const [isSpectator, setIsSpectator] = useState(false);

  const createGame = async (player1, isSinglePlayer = false) => {
    try {
      const response = await axios.post("http://localhost:5000/api/games", {
        player1,
      });
      setGameId(response.data.gameId);
      setPlayer(player1);
      setGameState(response.data);
      if (isSinglePlayer) {
        alert(
          `Game created! Playing against AI. Game ID: ${response.data.gameId}`
        );
      } else {
        alert(
          `Game created! Share this Game ID with your friend: ${response.data.gameId}`
        );
      }
    } catch (error) {
      console.error("Failed to create game:", error);
    }
  };

  const joinGame = async (gameId, playerName) => {
    try {
      const response = await axios.post(
        "http://localhost:5000/api/games/join",
        {
          gameId,
          player: playerName,
        }
      );
      setGameId(gameId);
      setPlayer(playerName);
      setGameState(response.data);
      alert(`Joined game successfully!`);
    } catch (error) {
      console.error("Failed to join game:", error);
      alert("Failed to join game. Make sure the Game ID is correct.");
    }
  };

  const spectateGame = async (gameId) => {
    try {
      const response = await axios.get(
        `http://localhost:5000/api/games/state/${gameId}`
      );
      setGameId(gameId);
      setGameState(response.data);
      setIsSpectator(true);
      alert(`Spectating game with ID: ${gameId}`);
    } catch (error) {
      console.error("Failed to spectate game:", error);
      alert("Failed to spectate game. Make sure the Game ID is correct.");
    }
  };

  const makeMove = async (position) => {
    if (isSpectator) {
      alert("Spectators cannot make moves.");
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:5000/api/games/move",
        {
          gameId,
          player,
          position,
        }
      );
      setGameState(response.data);
    } catch (error) {
      console.error("Failed to make move:", error);
      alert(
        "Failed to make move. Check if it's your turn or the position is valid."
      );
    }
  };

  return (
    <div className="App">
      <h1>Tic Tac Toe</h1>
      {!gameId && (
        <div>
          <h2>Create or Join a Game</h2>
          <button onClick={() => createGame("Player1", true)}>
            Play Against AI
          </button>
          <button onClick={() => createGame("Player1", false)}>
            Create Game (Multiplayer)
          </button>
          <div>
            <input
              type="text"
              placeholder="Enter Game ID"
              onChange={(e) => setGameId(e.target.value)}
            />
            <button onClick={() => joinGame(gameId, "Player2")}>
              Join Game
            </button>
            <button onClick={() => spectateGame(gameId)}>Spectate Game</button>
          </div>
        </div>
      )}
      {gameState && (
        <div>
          <h2>Game ID: {gameId}</h2>
          <h3>Status: {gameState.status}</h3>
          <h3>Current Turn: {gameState.currentTurn}</h3>
          <Board board={gameState.board} makeMove={makeMove} />
          {gameState.status === "finished" && (
            <h3>
              Winner:{" "}
              {gameState.winner === "draw" ? "It's a draw!" : gameState.winner}
            </h3>
          )}
        </div>
      )}
    </div>
  );
};

export default App;
